function isValidUrl(e){if(notEmpty(urlQueryStore[e]))return urlQueryStore[e];yqlLookup(e,function(t){urlQueryStore[e]=notEmpty(t.query.results);return urlQueryStore[e]});return"requested"}function yqlLookup(e,t){$.ajax({url:"http://query.yahooapis.com/v1/public/yql",jsonp:"callback",dataType:"jsonp",data:{q:'select * from html where url="'+e+'" and xpath="//body"',format:"json"},success:function(e){return t(e)}})}function domainLookup(e,t){$.get("http://domai.nr/api/json/info?q="+e,function(e){return t(e)})}var urlQueryStore={},urlify=function(e){return"http://"+e},notEmpty=function(e){return e!=undefined&&e!=null};