/*
 * Twitter Handler
 * Check if a Twitter handle is available
 * 
 * isValidHandle(handle)
 */// Key-Value pairing for Twitter handle occupancy
function isValidHandle(e){if(notEmpty(twitterStore[e]))return twitterStore[e];twitterLookup(e,function(t){twitterStore[e]=notEmpty(t.query.results);return twitterStore[e]});return"requested"}function twitterLookup(e,t){$.ajax({url:"http://query.yahooapis.com/v1/public/yql",jsonp:"callback",dataType:"jsonp",data:{q:'select * from html where url="http://twitter.com/'+e+'" and xpath=\'//div[contains(concat("",normalize-space(@class),""),"profile-card")]\'',format:"json"},success:function(e){return t(e)}})}var twitterStore={},notEmpty=function(e){return e!=undefined&&e!=null};