var hideAddressBar=function(){if(!window.location.hash){document.height<window.outerHeight&&(document.body.style.height=window.outerHeight+50+"px");setTimeout(function(){window.scrollTo(0,1)},50)}};window.addEventListener("load",function(){window.pageYOffset||hideAddressBar()});window.addEventListener("orientationchange",hideAddressBar);$("#search").focus(function(){$("body").addClass("search");window.scrollTo(0,1)});